CREATE TABLE mymenus_menu (  id int(5) unsigned NOT NULL auto_increment,  pid int(5) unsigned NOT NULL default '0',  mid int(5) unsigned NOT NULL default '0',  title varchar(150) NOT NULL default '',  alt_title varchar(150) NOT NULL default '',  visible tinyint(1) unsigned NOT NULL default '0',  link varchar(255) default NULL,  weight tinyint(4) unsigned NOT NULL default '0',  target varchar(10) default NULL,  groups text default NULL,  hooks text default NULL,  image varchar(255) default NULL,  css varchar(255) default NULL,  PRIMARY KEY (id)) ENGINE=MyISAM;CREATE TABLE mymenus_menus (  id int(5) unsigned NOT NULL auto_increment,  title varchar(150) NOT NULL default '',  PRIMARY KEY (id)) ENGINE=MyISAM;